#!/usr/bin/env bash
GFCU()
{
grubfile=$(find /boot -type f \( -name 'grubenv' -o -name 'grub.conf' -o -name 'grub.cfg' \) -exec grep -Pl '^\h*(kernelopts=|linux|kernel)' {} \;)
grubdir=$(dirname "$grubfile")
grub2-mkconfig -o "$grubdir/grub.cfg"
}
GFCU

