sql57_object.sql:
select LEFT(variable_value, char_length(variable_value)-1) as variable_value
FROM (
select VARIABLE_VALUE 
from information_schema.GLOBAL_VARIABLES
where variable_name like '%datadir%'
) as variable_value_result;

select LEFT(variable_value, char_length(variable_value)-1) as variable_value
FROM (
select VARIABLE_VALUE 
from information_schema.GLOBAL_VARIABLES
where variable_name like '%plugin_dir%'
) as variable_value_result;
